import type { Meta, StoryObj } from '@storybook/react'

import { useState } from 'react'

import { Icon } from '@/components/ui/icon/Icon'
import { Rating } from '@/components/ui/rating'
import { Typography } from '@/components/ui/typography'

import s from './table.module.scss'

import { Table } from './'
import {Button} from "@/components/ui/button";

const meta = {
  component: Table.Root,
  tags: ['autodocs'],
  title: 'Components/Table',
} satisfies Meta<typeof Table.Root>

export default meta
type Story = StoryObj<typeof meta>

const data: DataType[] = [
  {
    author: 'Lady Gaga',
    category: 'Movies',
    date: '30.08.2023',
    id: '01',
    imgPath:
      'data:image/jpeg;base64,',
    name: '2Movies 90s',
    rating: 4,
  },
  {
    author: 'Rihanna',
    category: 'Music',
    date: '30.09.2023',
    id: '02',
    imgPath:
      'data:image/jpeg;base64,',
    name: '1Busic 80s',
    rating: 3,
  },
  {
    author: 'Katy Perry',
    category: 'Geography',
    date: '30.10.2023',
    id: '03',
    imgPath:
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQs6AOU5X8xYP2gpPktsFNoxrSRKKTaH_gXnw&usqp=CAU',
    name: '3Cities',
    rating: 5,
  },
]

const columns: ColumnsType[] = [
  { id: 'name', label: 'Name' },
  { id: 'category', label: 'Category' },
  { id: 'author', label: 'Author' },
  { id: 'date', label: 'Date' },
  { id: 'rating', label: 'Rating' },
]

export const Default: Story = {
  args: {
    columns,
    data,
  },
  render: args => {
    const [currentColumnId, setCurrentColumnId] = useState<null | string>(null)
    const [currentSortDirection, setCurrentSortDirection] = useState<null | string>(null)
    const [dataList, setDataList] = useState<DataType[]>(args.data)

    const handleColumnSort = (columnId: string) => {
      let sortDirection = currentSortDirection

      if (currentColumnId !== columnId) {
        setCurrentColumnId(columnId)
        sortDirection = null
      }

      const nextSortDirection = getNextSortDirection(sortDirection)

      setCurrentSortDirection(nextSortDirection)

      sortDataByColumn(columnId, nextSortDirection)
    }

    const getNextSortDirection = (currentSortDirection: null | string) => {
      switch (currentSortDirection) {
        case null:
          return 'asc'
        case 'asc':
          return 'desc'
        default:
          return null
      }
    }

    const sortDataByColumn = (columnId: string, nextSortDirection: null | string) => {
      if (nextSortDirection === 'asc') {
        const newData = [...dataList].sort((a, b) =>
          a[columnId as keyof typeof a] > b[columnId as keyof typeof a] ? 1 : -1
        )

        setDataList(newData)
      } else if (nextSortDirection === 'desc') {
        const newData = [...dataList].sort((a, b) =>
          b[columnId as keyof typeof a] > a[columnId as keyof typeof a] ? 1 : -1
        )

        setDataList(newData)
      } else {
        setDataList(args.data)
      }
    }

    return (
      <Table.Root {...args}>
        <Table.Head>
          <Table.Row>
            {columns.map(el => (
              <Table.HeadCell
                key={el.id}
                onClick={() => {
                  handleColumnSort(el.id)
                }}
              >
                <div className={s.divHeadCell}>
                  <Typography className={s.typographyStyleHead} variant={'subtitle2'}>
                    {el.label}
                  </Typography>
                  {currentSortDirection !== null && currentColumnId === el.id ? (
                    <div
                      className={`${s.arrow} ${currentSortDirection === 'desc' ? s.arrowDESC : ''}`}
                    >
                      <Icon height={'24px'} iconId={'Arrow'} width={'24px'} />
                    </div>
                  ) : (
                    <div className={s.divArrow}></div>
                  )}
                </div>
              </Table.HeadCell>
            ))}
            <Table.HeadCell className={s.headCellControl}>
              <div className={s.divHeadCellControl}>
                <Typography className={s.typographyStyleHeadControl} variant={'subtitle2'}>
                  Control
                </Typography>
              </div>
            </Table.HeadCell>
          </Table.Row>
        </Table.Head>
        <Table.Body>
          {dataList.map(el => (
            <Table.Row key={el.id}>
              <Table.Cell>
                <div className={s.divNameBody}>
                  <img alt={'img'} height={60} src={el.imgPath} width={118} />
                  <Typography className={s.typographyStyleBody} variant={'body2'}>
                    {el.name}
                  </Typography>
                </div>
              </Table.Cell>
              <Table.Cell>
                <Typography className={s.typographyStyleBody} variant={'body2'}>
                  {el.category}
                </Typography>
              </Table.Cell>
              <Table.Cell>
                <Typography className={s.typographyStyleBody} variant={'body2'}>
                  {el.author}
                </Typography>
              </Table.Cell>
              <Table.Cell>
                <Typography className={s.typographyStyleBody} variant={'body2'}>
                  {el.date}
                </Typography>
              </Table.Cell>
              <Table.Cell>
                <div className={s.rating}>
                  <Rating defaultValue={el.rating}></Rating>
                </div>
              </Table.Cell>
              <Table.Cell>
                <div className={s.control}>
                  <Button variant={'pure'} onClick={() => alert('play')}>
                    <Icon height={'16px'} iconId={'Learn'} width={'16px'} />
                  </Button>
                  <Button variant={'pure'} onClick={() => alert('play')}>
                    <Icon height={'16px'} iconId={'Edit'} width={'16px'} />
                  </Button>
                  <Button variant={'pure'} onClick={() => alert('play')}>
                    <Icon height={'16px'} iconId={'Delete'} width={'16px'} />
                  </Button>
                </div>
              </Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table.Root>
    )
  },
}

type DataType = {
  author: string
  category: string
  date: string
  id: string
  imgPath: string
  name: string
  rating: number
}

type ColumnsType = {
  id: string
  label: string
}
